#!/bin/bash

# Script to generate the lcov information file

# Build folder
export FOLDER=~/Programs/CTF-std

# lcov command
lcov --rc geninfo_gcov_all_blocks=0 --directory ${FOLDER}/cobra_tf/ctf_src/CMakeFiles/cobra_tf.dir/ --capture --output-file ~/coverage.info 

# Coverage .info is the coverage file generated by lcov and will be needed by gcov

echo "Coverage Files Generatred"


